import { FETCH_PRODUCTS } from "../types";
 
export const fetchProducts = () => dispatch => {
  fetch("http://localhost:3000")
    .then(res => res.data.json())
    .then(data => {
      return dispatch({
        type: FETCH_PRODUCTS,
        payload: data.json
      });
    }); 
};
