import {createStore, applyMiddleware, combineReducers} from "redux";
import thunk from "redux-thunk";
import { productsReducer } from "./reducers/productReducers";


const initialState = {};

const store = createStore(
    combineReducers({
    products: productsReducer,
 }),
 initialState,
 );
